pragma solidity >= 0.6.0;
pragma experimental ABIEncoderV2; // Habilitar ABIEncoderV2

contract WalletWall {
    struct Message {
        address sender;
        string content;
    }

    Message[] public messages;
    address payable public owner;

    event MessagePublished(address sender, string content);

    constructor() public {
        owner = payable(msg.sender);
    }

    function publish(string memory content) public payable {
        uint256 gasCost = tx.gasprice * 10; // Estimación aproximada del costo de gas para una transacción simple

        require(msg.value >= 2 * gasCost, "Insufficient payment: must pay for gas and app fee.");

        owner.transfer(msg.value / 2); // Envía la mitad del pago al propietario (la tarifa de la app)

        Message memory newMessage = Message({
            sender: msg.sender,
            content: content
        });
        messages.push(newMessage);

        emit MessagePublished(msg.sender, content);
    }

    function getMessages() public view returns (Message[] memory) {
        return messages;
    }
}
